# -*- coding: utf-8 -*-
"""
Various utiltiy and testing functions
"""
import numpy as np
import cv2
import os
from numpy_sift import SIFTDescriptor 
from nose.tools import assert_equal, ok_
from IPython.display import display, HTML, clear_output

psetLabels = []

def test_ok():
    """ If execution gets to this point, print out a happy message """
    try:
        from IPython.display import display_html
        display_html("""<div class="alert alert-success">
        <strong>Tests passed!!</strong>
        </div>""", raw=True)
    except:
        print("Tests passed!!")

def test_patch_entropy_solution(get_patch_entropy):
    test_probs = [-.1, -0, -.5, -.4, -.3, -1, -0, -.5, -.7]
    assert get_patch_entropy(test_probs) - 0.3888888888888889 < 0.0001, "Total patch entropy is incorrect"
    return "Tests passed!"

def test_MSER(MSER):
    correct80 = np.load('80.npy')
    assert np.array_equal(MSER(80), correct80), "Incorrect thresholding"
    correct100 = np.load('100.npy')
    assert np.array_equal(MSER(100), correct100), "Incorrect thresholding"
    return "Tests passed!"

